<template>
  <div>
    <div class="container" ref="containerRef"></div>
  </div>
</template>

<script setup lang="ts">
import { onMounted, ref } from "vue";
import LogicFlow from '@logicflow/core';
import { BezierEdge, BezierEdgeModel } from '@logicflow/core';
import { SelectionSelect,MiniMap } from '@logicflow/extension';
import { DndPanelVue } from "./extension/dnd-panel-vue";
import { Control } from "./extension/control-vue.ts";
import AndGate from "./components/baseGate/AndGate.ts";
import Input from "./components/io/Input.ts";
import Output from "./components/io/Output.ts";

const containerRef = ref(); // 画布容器引用
const simulationActive = ref(false); // 仿真按钮的状态
const inputActive = ref(false); // 输入是否被点击
const edgeStatus = ref(0); // 连线的状态
const dndData = ref([
  {
    group: "基本逻辑门",
    items: [
      { id: '1', type: 'AndGate', label: '与门', properties: {}, icon: 'data:image/svg+xml;base64,' },
      { id: '2', type: 'AndGate', label: '或门', properties: {}, icon: 'data:image/svg+xml;base64,' },
      { id: '3', type: 'AndGate', label: '非门', properties: {}, icon: 'data:image/svg+xml;base64,' },
      { id: '4', type: 'rect', label: '矩形', properties: {}, icon: 'data:image/svg+xml;base64,' },
    ]
  },
  {
    group: "输入输出",
    items: [
      { id: '1', type: 'Input', label: '输入', icon: 'data:image/svg+xml;base64,' },
      { id: '2', type: 'Output', label: '输出', icon: 'data:image/svg+xml;base64,', }
    ]
  },
  {
    group: "触发器",
    items: [
      { id: '2', type: 'Input', label: '输入', icon: 'data:image/png;base64,' },
      { id: '3', type: 'Output', label: '输出', icon: 'data:image/png;base64,' },
    ]
  },
  {
    group: "存储器",
    items: [
      { id: '2', type: 'Input', label: '输入', icon: 'data:image/png;base64,' },
      { id: '3', type: 'Output', label: '输出', icon: 'data:image/png;base64,' },
    ]
  },
  {
    group: "运算器",
    items: [
      { id: '2', type: 'Input', label: '输入', icon: 'data:image/png;base64,' },
      { id: '3', type: 'Output', label: '输出', icon: 'data:image/png;base64,' },
    ]
  }
]) // 菜单列表

// Mounted钩子
onMounted(() => {
  // 1.LogicFlow初始化
  let lf = lfInit()

  // 2.注册节点
  lf = registerNodes(lf, [AndGate, Input, Output])

  // 3.设置插件
  lf = setPlugins(lf);

  // 4.监听连线和节点事件
  lf = onEdgeConnected(lf);
  lf = onNodeClick(lf);

  // 5.渲染lf
  renderLF(lf);
})

// LogicFlow初始化
const lfInit = () => {
  // 创建容器
  const lf = new LogicFlow({
    container: containerRef.value,
    grid: true, // 使用网格
    stopMoveGraph: true, // 画布不允许移动
    plugins: [DndPanelVue, Control, SelectionSelect, MiniMap],
    keyboard: {
      enabled: true, // 开启快捷键
    }
  });

  // 设置连线箭头
  lf.setTheme({
    arrow: {
      offset: 0,
      verticalLength: 0,
    },
  });

  // 连线为曲线
  lf.register({
    type: 'bezier',
    model: BezierEdgeModel,
    view: BezierEdge
  });
  lf.setDefaultEdgeType('bezier');

  // 修改对齐辅助线样式
  lf.setTheme({
    snapline: {
      stroke: '#1E90FF', // 对齐线颜色
      strokeWidth: 1, // 对齐线宽度
    },
  })

  return lf;
}

// 注册节点
const registerNodes = (lf: LogicFlow, nodes: any[]) => {
  nodes.forEach((item) => {
    lf.register(item);
  })
  return lf
}

// 设置插件
const setPlugins = (lf: LogicFlow) => {
  // 设置拖拽插件dndPanelVue
  lf = setDndPanelVuePlugin(lf)

  // 设置控制面板插件Control
  lf = setControlPlugin(lf)
  return lf;
}

// 设置拖拽插件dndPanelVue
const setDndPanelVuePlugin = (lf: LogicFlow) => {
  lf.extension.dndPanelVue.setPatternItems(dndData.value)
  return lf;
}

// 设置控制面板插件Control
const setControlPlugin = (lf: LogicFlow) => {
  // 添加仿真按钮
  lf.extension.control.addItem({
    key: 'simulation',
    // TODO: 现在切换还没实现
    iconClass: simulationActive.value ? "fa fa-pause" : "fa fa-play", // 在html引入了font样式
    text: "仿真",
    /* @ts-ignore */
    onClick: (lf: LogicFlow, ev: any) => {
      // 切换状态
      simulationActive.value = !simulationActive.value;
      // 获取所有节点
      lf = lf.lf; // bug-#IB3T6D : https://gitee.com/lonelyzoom/online-logisim/issues/IB3T6D
      const nodes = lf.graphModel.nodes;

      // 遍历所有节点，并更新每个节点的status属性
      // simulation代表处于仿真状态
      // normal代表处于一般状态
      // 此处更改会导致对应节点的getShape处改变节点形状
      nodes.forEach((node: { id: any; }) => {
        lf.setProperties(node.id, {
          status: simulationActive.value ? 'simulation' : 'normal',
        });
      });
    },
  })
  // 添加导航按钮
  lf.extension.control.addItem({
    key: 'guide',
    iconClass: "fa fa-eye",
    title: "导航",
    text: "导航",
    /* @ts-ignore */
    onClick: (lf: any, ev: any) => {
      // 指定小地图显示在特定位置
      lf.extension.miniMap.show(containerRef.value.offsetWidth - 170, containerRef.value.offsetHeight - 320);
    }
  })
  // 移除适应/上一步/下一步按钮
  lf.extension.control.removeItem('reset')
  lf.extension.control.removeItem('undo')
  lf.extension.control.removeItem('redo')
  return lf;
}

// 监听边的创建事件
const onEdgeConnected = (lf: LogicFlow) => {
  // TODO: 确定连接点之间是否允许连接
  // lf.on("edge:add", (edge) => {
  //   const { id, sourceNodeId, targetNodeId } = edge.data
  //   console.log(id, sourceNodeId, targetNodeId )
  //   lf.updateAttributes(id,{
  //     style:{
  //       stroke: 'green', // 设置边的颜色为绿色
  //     }
  //   })
  // })
  return lf;
}

// 渲染lf
const renderLF = (lf: LogicFlow) => {
  lf.render({
    nodes: [],
    edges: [],
  });

  // MiniMap.show()必须在lf.render()后调用。
  lf.extension.miniMap.show(containerRef.value.offsetWidth - 170, containerRef.value.offsetHeight - 320)
}

// 监听节点点击
const onNodeClick = (lf: LogicFlow) => {
  lf.on('node:click', (node) => {
    const { type, properties } = node.data;
    if(properties.status === 'simulation'){
      if(type === "Input"){
        const edgeId = lf.graphModel.edges[0].id
        const sourceNodeId = lf.graphModel.edges[0].sourceNodeId
        const targetNodeId = lf.graphModel.edges[0].targetNodeId
        if(edgeStatus.value === 1){
          edgeStatus.value = 0;
        }else{
          edgeStatus.value = 1;
        }

        // 设置边的颜色为绿色
        lf.updateAttributes(edgeId,{
          style:{
            stroke: (edgeStatus.value === 1) ? 'green' : 'black'
          }
        })
        // 设置2节点的颜色为绿色
        lf.setProperties(sourceNodeId, {
          clicked: !inputActive.value
        });
        lf.setProperties(targetNodeId, {
          clicked: !inputActive.value
        });
        inputActive.value = !inputActive.value
      }
    }
  })
  return lf;
}
</script>

<style scoped>
.container {
  display: flex;
  height: 100vh;
}

.lf-container {
  flex: 1;
  border: 1px solid #ccc;
}
</style>
