<template>
  <div>
    <div class="container" ref="containerRef"></div>
  </div>
</template>

<script setup lang="ts">
import { onMounted,ref } from "vue";
import LogicFlow from '@logicflow/core';
import { Control, SelectionSelect } from '@logicflow/extension';
import { DndPanelVue } from "./extension/dnd-panel-vue";

import AndGate from "./components/baseGate/AndGate.ts";
import Input from "./components/io/Input.ts";
import Output from "./components/io/Output.ts";

import './extension/dnd-panel.ts'

LogicFlow.use(DndPanelVue);
LogicFlow.use(Control);
LogicFlow.use(SelectionSelect);

// 加载完成时
const containerRef = ref(); // 画布容器引用

// 菜单列表
const dndData = [
  {
    group: "基本逻辑门",
    items:[
      { id: '1', type: 'AndGate', label: '与门',icon: 'data:image/png;base64,'  },
    ]
  },
  {
    group: "输入输出",
    items:[
      { id: '1', type: 'AndGate', label: '与门',icon: 'data:image/png;base64,'  },
      { id: '2', type: 'Input', label: '输入' ,icon: 'data:image/png;base64,'  },
      { id: '3', type: 'Output', label: '输出' , icon: 'data:image/png;base64,'  },
      { id: '4', type: 'Output', label: '输出' , icon: 'data:image/png;base64,'  },
      { id: '5', type: 'Output', label: '输出' , icon: 'data:image/png;base64,'  },
      { id: '6', type: 'Output', label: '输出' , icon: 'data:image/png;base64,'  },
    ]
  },
  {
    group: "触发器",
    items:[
      { id: '2', type: 'Input', label: '输入' ,icon: 'data:image/png;base64,'  },
      { id: '3', type: 'Output', label: '输出' , icon: 'data:image/png;base64,'  },
    ]
  },
  {
    group: "存储器",
    items:[
      { id: '2', type: 'Input', label: '输入' ,icon: 'data:image/png;base64,'  },
      { id: '3', type: 'Output', label: '输出' , icon: 'data:image/png;base64,'  },
    ]
  },
  {
    group: "运算器",
    items:[
      { id: '2', type: 'Input', label: '输入' ,icon: 'data:image/png;base64,'  },
      { id: '3', type: 'Output', label: '输出' , icon: 'data:image/png;base64,'  },
    ]
  }
]

// 加载成功
onMounted(() => {
  // 找到容器
  const lf = new LogicFlow({
    container: containerRef.value,
    grid: true,
    stopMoveGraph: true,
  });

  // 2.自定义节点
  lf.register(AndGate);
  lf.register(Input);
  lf.register(Output);

  // 3.内部组件
  lf.extension.dndPanelVue.setPatternItems(dndData)
  lf.extension.selectionSelect.openSelectionSelect();
  // 修改对齐线样式
  lf.setTheme({
    snapline: {
      stroke: '#1E90FF', // 对齐线颜色
      strokeWidth: 1, // 对齐线宽度
    },
  })

  // 4.渲染
  lf.render({
    nodes: [],
    edges: [],
  });

  // 5.各节点数据
  const data = lf.getGraphData();
  console.log(data)
})

</script>

<style scoped>
.container {
  display: flex;
  height: 100vh;
}
.lf-container {
  flex: 1;
  border: 1px solid #ccc;
}
</style>
